const rawAction = webhookData.action || $json.action;
let action = rawAction;
if (typeof action === 'string') {
  action = action.trim().toLowerCase();
}
const rawEventId = webhookData.event_id || $json.event_id;
const eventId = parseInt(rawEventId) || null;
const rawGuestIds = webhookData.guest_ids || $json.guest_ids;
let guestIds = [];
if (Array.isArray(rawGuestIds)) {
  guestIds = rawGuestIds.map(id => parseInt(id)).filter(id => !isNaN(id));
}

const validActions = ['send_global_all', 'send_event_all', 'send_selected'];
if (!validActions.includes(action)) {
  if (action.includes('global')) {
    action = 'send_global_all';
  } else if (action.includes('event')) {
    action = 'send_event_all';
  } else if (action.includes('selected')) {
    action = 'send_selected';
  } else {
    throw new Error(`‚ùå Unknown action: ${action}`);
  }
}

if ((action === 'send_event_all' || action === 'send_selected') && !eventId) {
  throw new Error('‚ùå Event ID ŸÖÿ∑ŸÑŸàÿ® ŸÑŸáÿ∞ÿß ÿßŸÑŸÜŸàÿπ ŸÖŸÜ ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ');
}

if (action === 'send_selected' && guestIds.length === 0) {
  throw new Error('‚ùå Guest IDs ŸÖÿ∑ŸÑŸàÿ®ÿ© ŸÑŸÑÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑŸÖÿ≠ÿØÿØ');
}

const result = {
  action: action,
  eventId: eventId,
  guestIds: guestIds,
  timestamp: Date.now(),
  isGlobal: action === 'send_global_all',
  isEventSpecific: action === 'send_event_all',
  isSelectedGuests: action === 'send_selected',
  validationPassed: true,
  processedAt: new Date().toISOString()
};

return [{ json: result }];

